import {Header, Titulo, ContenedorHeader, Subtitulo} from '../elementos/Header';
import Boton from '../elementos/Boton';
import React, {useState} from 'react';
import {Helmet} from 'react-helmet';
import {Formulario, Input, ContenedorBoton} from '../elementos/ElementosDeFormulario'
import {useNavigate} from 'react-router-dom';
import styled from 'styled-components';
import pantera1 from '../imagenes/Pantera1.png'

const ImagenLogo1 = styled.img`
    width: 40%; /* La imagen es un 30% más pequeña */
    margin-top: -8%; /* Puedes ajustar el valor según sea necesario */

    @media (max-width: 768px) {
        margin-left: 0; /* Elimina el margen izquierdo en pantallas pequeñas */
        width: 92%; /* Ocupa todo el ancho del contenedor en pantallas pequeñas */
    }

`;


const ContenedorTitulo = styled.div`
	height: 100%;
	width:100%;
	margin-left:9%;

    display: flex;
    align-items: center;
    justify-content: flex-start;

`;


const RegistroUsuarios =() =>{
    return(
        <>
          <Helmet>
				<title>Registrar Usuarios</title>
			</Helmet>

            <Header>
				<ContenedorHeader>
					<Titulo>Registro de Usuarios</Titulo>

				</ContenedorHeader>
			</Header>
			<ContenedorTitulo>
        <ImagenLogo1 src={pantera1} alt="LogoUam" />
      </ContenedorTitulo>
      <Subtitulo>Registro de Alumnos</Subtitulo>

        </>
    );

}

export default RegistroUsuarios;
