import {Header, Titulo, ContenedorHeader} from '../elementos/Header';
import Boton from '../elementos/Boton';
import React, {useState} from 'react';
import {Helmet} from 'react-helmet';
import {Formulario, Input, ContenedorBoton} from '../elementos/ElementosDeFormulario'
import {useNavigate} from 'react-router-dom';
import styled from 'styled-components';
import imagen from '../imagenes/variacion5Cua.png'

const ImagenLogo1 = styled.img`
    width: 40%; /* La imagen es un 30% más pequeña */
    margin-left:-2%; /*Se mueve a la derecha*/
    margin-top: 20px; /* Puedes ajustar el valor según sea necesario */

    @media (max-width: 768px) {
        margin-left: 0; /* Elimina el margen izquierdo en pantallas pequeñas */
        width: 92%; /* Ocupa todo el ancho del contenedor en pantallas pequeñas */
    }

`;


const ContenedorTitulo = styled.div`
	width:50%;
    display: flex;
    align-items: center;
    justify-content: flex-start;
    margin-left: 20%; /* Espacio del 10% a la izquierda */

`;

const InicioSesion = () =>{
	const navigate = useNavigate();
	const [correo, establecerCorreo] = useState('');
	const [password, establecerPassword] = useState('');
	const [estadoAlerta, cambiarEstadoAlerta] = useState(false);
	const [alerta, cambiarAlerta] = useState({});

    return (
        <>
           <Helmet>
				<title>Inicia Sesión</title>
			</Helmet>
            <Header>
				<ContenedorHeader>
					<Titulo>Iniciar Sesión</Titulo>
					<div>
						<Boton to="/crear-cuenta">Registrate</Boton>
					</div>
				</ContenedorHeader>
			</Header>
			<ContenedorTitulo>
                <ImagenLogo1 src={imagen} alt="LogoUam" />
            </ContenedorTitulo>
			<Formulario >

				<Input 
					type="email"
					name="email"
					placeholder="Correo Electrónico"
					value={correo}

				/>
				<Input 
					type="password"
					name="password"
					placeholder="Contraseña"
					value={password}

				/>
				<ContenedorBoton>
					<Boton as="button" primario type="submit">Iniciar Sesión</Boton>
				</ContenedorBoton>
			</Formulario>

        </>
    );
}
export default InicioSesion;
