import styled from 'styled-components';
import theme from './../theme';

const ContenedorFiltros = styled.div`
    display: flex;
    justify-content: space-between;
    margin-bottom: 1.87rem; /* 30px */
 
    @media(max-width: 60rem){ /* 950px */
        flex-direction: column;
 
        & > * {
            width: 100%;
            margin-bottom: 0.62rem; /* 10px */
        }
    }
`;
 
const Formulario = styled.form`
    padding: 0 2.5rem; /* 40px */
    height: 100%;
    display: flex;
    flex-direction: column;
    justify-content: space-around;
    input {
        width: 100%;
        text-align: center;
        padding: 2.5rem 0;
        font-family: 'Work Sans', sans-serif;
        &::placeholder {
            color: rgba(0,0,0,.2);
        }
    }
 
    @media(max-width: 60rem){ /* 950px */
        justify-content: start;
    }
`;
 
const Input = styled.input`
    font-size: 1.5rem; /* 40px */
   
    border: none;
    border-bottom: 2px solid ${theme.grisClaro};
    outline: none;
 
    @media(max-width: 60rem){ /* 950px */
        font-size: 2.2rem; /* 24px */
    }
`;
 
const InputGrande = styled(Input)`
    font-size: 4.37rem; /* 70px */
    font-weight: bold;
`;
 
const ContenedorBoton = styled.div`
    display: flex;
    justify-content: center;
    margin: 2.5rem 0;  /* 40px */
`;

const FormularioRegistro = styled.form`
   const FormularioRegistro = styled.form
    padding: 2rem;
    background-color: #ff4d4d; /* Fondo rojo */
    color: #fff;
    border-radius: 8px;
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 2rem; /* Espaciado entre secciones */

    h2 {
        font-size: 2.5rem;
        font-weight: bold;
        text-align: center;
        margin-bottom: 1rem;
    }

    .contenedor-inputs {
        width: 100%;
        display: grid;
        grid-template-columns: repeat(3, 1fr); /* Tres columnas */
        gap: 1rem; /* Espaciado entre inputs */

        label {
            display: flex;
            flex-direction: column;
            font-size: 1rem;
            color: #000; /* Texto negro para etiquetas */
        }

        input, select {
            width: 100%;
            padding: 0.8rem;
            border: 1px solid #000;
            border-radius: 4px;
            font-size: 1rem;
            color: #000;
            background-color: #fff;

            &::placeholder {
                color: #aaa;
            }
        }
    }

    .contenedor-botones {
        display: flex;
        justify-content: center;
        gap: 1rem;

        button {
            padding: 0.8rem 2rem;
            background-color: #000;
            color: #fff;
            border: none;
            border-radius: 4px;
            font-size: 1rem;
            cursor: pointer;
            transition: all 0.3s ease;

            &:hover {
                background-color: #333;
            }
        }
    }

    .contenedor-imagen {
        width: 100%;
        background-color: #ccc; /* Fondo gris claro */
        display: flex;
        justify-content: center;
        align-items: center;
        border: 1px dashed #000;
        height: 200px;
        img {
            max-width: 100%;
            max-height: 100%;
        }
    }


`;
 


export {ContenedorFiltros, Formulario, Input, InputGrande, ContenedorBoton,FormularioRegistro};
