import {Header, Titulo, ContenedorHeader, Subtitulo} from '../elementos/Header';
import Boton from '../elementos/Boton';
import React, {useState} from 'react';
import {Helmet} from 'react-helmet'; 
import {useNavigate} from 'react-router-dom';
import styled from 'styled-components';
import pantera1 from '../imagenes/Pantera1.png'

const ImagenLogo1 = styled.img`
    margin-right: 2%;
    width: 35%; /* La imagen es un 30% más pequeña */
    @media (max-width: 768px) {
       margin-top: -400px; 
        width: 40%; /* Ocupa todo el ancho del contenedor en pantallas pequeñas */
    }

`;


const ContenedorImagen = styled.div`
	height: 100%;
	width:100%;
	margin-left:13%;
  display: flex;
  align-items: center;
  justify-content: flex-start;
`;

const ContenedorBotonRegistro = styled.div`
	  display: flex;
    justify-content: center;
    margin: 2% ;  /* 40px */
    gap: 100px; /* Espacio entre los botones */
`;



const HomeCoordinador =() =>{
   const navigate = useNavigate();

    return(
        <>
          <Helmet>
				<title>Inicio de Empleado</title>
			</Helmet>

        <Header>
				<ContenedorHeader>
					<Titulo>Inicio de Empleado</Titulo>
					</ContenedorHeader>
			</Header>
			<ContenedorImagen>
      <ImagenLogo1 src={pantera1} alt="LogoUam" />
      <ImagenLogo1 src={pantera1} alt="LogoUam" />
      </ContenedorImagen>
      <ContenedorBotonRegistro>
					<Boton as="button" primario type="submit"> Materiales Disponibles</Boton>
					<Boton as="button" primario type="submit"> Historial</Boton>
			</ContenedorBotonRegistro>

      <ContenedorImagen>
      <ImagenLogo1 src={pantera1} alt="LogoUam" />
      <ImagenLogo1 src={pantera1} alt="LogoUam" />
      </ContenedorImagen>
      <ContenedorBotonRegistro>
					<Boton as="button" primario type="submit" onClick={() => navigate("/registro-usuarios")}>Registrar Usuario</Boton>
					<Boton as="button" primario type="submit">Actualizar Contraseña Usuario</Boton>
			</ContenedorBotonRegistro>
        </>
    );

}

export default HomeCoordinador;
