import {Header, Titulo, ContenedorHeader, Subtitulo} from '../elementos/Header';
import Boton from '../elementos/Boton';
import React, {useState} from 'react';
import {Helmet} from 'react-helmet'; 
import {useNavigate} from 'react-router-dom';
import styled from 'styled-components';
import {TitutuloSecciones,FormularioRegistroSecciones,Select,Input2, ContenedorBoton,FormularioRegistro} from '../elementos/ElementosDeFormulario'
import imagen1 from '../imagenes/motasPantera4.png'

const ImagenMotas = styled.img`
  position: absolute;
  top: 12%; /* Ajustar según el diseño */
  left: 76%;
  width: 30%;
  height: 80%;
  z-index: -1;
  object-fit: cover; /* Ajusta cómo se muestra la imagen dentro del contenedor */
  object-position: center; /* Centra la imagen si se recorta */
  clip-path: inset(0 10px 0 10px); /* Recorte adicional si es necesario */

  @media (max-width: 768px) {
    margin-left: 0;
    width: 24%;
    height: 80%;
    left: 76%;
  }
`;

const ContenedorImagen = styled.div`
  position: relative; /* Posición relativa para que la imagen esté dentro del flujo */
  width: 20%; /* Ajusta según el espacio disponible */
  height: 300px; /* Ajusta la altura máxima */
  overflow: hidden; /* Oculta las partes de la imagen fuera del contenedor */
`;



const RecuperaContraseña = () => {
    return (  
        <>
        <Helmet>
              <title>Recuperar Contraseña</title>
          </Helmet>

        <Header>
              <ContenedorHeader>
                  <Titulo>Recuperar Contraseña</Titulo>
                  </ContenedorHeader>
        </Header>

            <ImagenMotas src={imagen1} alt="MotasUam" />

        <FormularioRegistro>
		<FormularioRegistroSecciones>
	<TitutuloSecciones>Alumno Encontrado</TitutuloSecciones>	
        Usuario<Input2  type="text" 
                name="nombre" 
                placeholder="Nombre(s): Ej: Jose" />
         Correo Institucional<Input2  type="email" 
                name="correoInstitucional" 
                placeholder="Correo Electrónico Institucional: Ej: jose.ruiz@cua.uam.mx" />
         Contraseña<Input2  type="password" 
                        name="password" 
                        placeholder="Contraseña" />
         Repite contraseña<Input2  type="password" 
                        name="repeatPassword" 
                        placeholder="Repetir Contraseña" />
     </FormularioRegistroSecciones>



	        <ContenedorBoton>
	                <Boton as="button" primario type="submit">Actualizar Contraseña</Boton>
		</ContenedorBoton>
	</FormularioRegistro>

      </>


    );
}

export default RecuperaContraseña
